import hljs from "highlight.js/lib/core";
import "highlight.js/styles/default.css";
import "highlight.js/styles/github.css";
import "highlight.js/lib/languages/java";

hljs.initHighlightingOnLoad();

interface ICodeSnippetProps {
  code: string;
  onClose?: () => void;
  visible: boolean;
}

const CodeSnippet = (props: ICodeSnippetProps) => {
  return (
    <>{
      props.visible ?  <div className="overflow-hidden  fixed top-0 left-0 w-full h-50vh flex justify-center items-center ">
      <div className="bg-white p-5 rounded-md w-1/2  text-center grid-cols-3 border-2 border-black shadow-2xl">
        <div>
          <span className="text-bold">Java Code: </span>
        </div>
        <div>
        <pre>
          <code className="language-java">{props.code}</code>
        </pre>
      </div>
        </div>
    </div> : undefined
    }
    </>
  );
};

export default CodeSnippet;
