import React from "react";

interface ISorterProps{
  length: number;
  type: string;
}

interface ISorterState{
  array: number[];
}

const getNumberArray = () =>{
  const a = [];
  for(let i=0; i<100;i = i+1){
    a.push(Math.floor(Math.random() * (100)));
  }
  return a;
}

class Sorter extends React.Component<ISorterProps, ISorterState>{
  constructor(props: ISorterProps){
    super(props);
    this.state = {
      array: getNumberArray()
    }
  }


  render(){
    return(
      <>
      <div>
        {
          this.state.array.map((al) => {
            return (<>
            <div className={`w-12 h-${al}`}/>
            </>)
          })
        }

      </div>
      </>
    )
  }
}

export default Sorter;
