import React from "react";

interface ISorterProps{
  array: number[];
  type: string;
}




class Sorter extends React.Component<ISorterProps, any>{
  render(){
    return(
      <>
      <div className="flex flex-row grid-row-layout">
        {
          this.props.array.map((al,index) => {
            const height = `${Math.min(al*5,400)}px`;
            return (
            <>
            <div id={`bar${index}`} key={`${al}${index}`} aria-label={`${al}`} style={{height}}  className={`border-2 border-black w-8 mx-0.5 bg-black`}/>
            </>)
          })
        }
      </div>
      </>
    )
  }
}

export default Sorter;
