interface ISortInfoCardProps{
  name: String;
}

const SortInfoCard = (props: ISortInfoCardProps) => {
  return (
    <>
    <div className="w-1/3 border-2 bg-infoCard border-black rounded-lg">
      <select name="sortSelector" id="sortSelector" placeholder="Select Sort Type">
        <option value="BUBBLE_SORT">Bubble Sort</option>
        <option value="INSERTION_SORT">Insertion Sort</option>
        <option value="MERGE_SORT">Merge Sort</option>
        <option value="QUICK_SORT">Quick Sort</option>
        <option value="SELECTION_SORT">Selection Sort</option>
      </select>
      <div className="flex flex-row ml-4 my-8">
          <h2 className="font-extrabold">Name:</h2>
          <span className="ml-2">{props.name}</span>
      </div>
    </div>
    </>
  )
}

export default SortInfoCard
