import React from "react";

interface ISorterProps{
  array: number[];
  type: string;
  sortClicked: boolean;
}

let isSortingComplete: boolean = true;

async function finishSorting(){
  console.log("Sorting.....");
  isSortingComplete = false;
  let i=0;
  while(!isSortingComplete){
    console.log(i++);
    if (i===5){
      isSortingComplete = true;
    }
  }


}

class Sorter extends React.Component<ISorterProps, any>{
  render(){

    if (this.props.sortClicked){
     finishSorting();
    }


    return(
      <>
      <div className="flex flex-row min-h-1/2">
        {
          this.props.array.map((al,index) => {
            const height = `${Math.min(al*5,400)}px`;
            return (
            <>
            <div id={`bar${index}`} key={`${al}${index}`} aria-label={`${al}`} style={{height}}  className={`border-2 border-black w-12 mx-0.5 bg-black`}/>
            </>)
          })
        }
      </div>
      </>
    )
  }
}

export default Sorter;
