import hljs from "highlight.js/lib/core";
import "highlight.js/styles/default.css";
import "highlight.js/styles/github.css";
import "highlight.js/lib/languages/java";

hljs.initHighlightingOnLoad();

interface ICodeSnippetProps {
  code: string;
  onClose?: () => void;
  visible: boolean;
}

const CodeSnippet = (props: ICodeSnippetProps) => {

  if(props.visible){
    document.body.style.overflow = 'hidden';
  }
  else {
    document.body.style.overflow = 'auto';
  }

  return (
    <>{
      props.visible ?  <div className="fixed top-20 shadow-snippet m-4  overflow-hidden left-0 w-full flex justify-center items-center ">
      <div className=" bg-white rounded-md  text-center flex flex-col justify-evenly border-2 border-black">
        <div className="self-start mb-4">
          <span className="font-bold">Java Code: </span>
        </div>
        <div className="overflow-y-auto max-h-96 border-2 w-snippet bg-white  rounded-lg m-4">
        <pre>
          <code className="language-java">{props.code}</code>
        </pre>
      </div>
        </div>
    </div> : undefined
    }
    </>
  );
};

export default CodeSnippet;
