import React from 'react'
import Button from './Button'
import Sorter from './Sorter'


interface ISortVisualizerProps{
  sortName: string;
}

interface ISortVisualizerState{
  sortCount: number;
  sortArray: number[];
  sortClicked: boolean;
}

const getNumberArray = (length?: number) =>{
  const a: number[] = [];
  const arrayLength = length ?? 50;
  for(let i=0; i<arrayLength;i = i+1){
    a.push(Math.floor(Math.random() * (100)));
  }
  return a;
}

class SortVisualizer extends React.Component<ISortVisualizerProps, ISortVisualizerState>{
  constructor(props: ISortVisualizerProps){
    super(props);
    this.state = {
      sortCount: 25,
      sortArray: getNumberArray(25),
      sortClicked: false
    };
  }
   handleButtonClick = (e: any, buttonClicked: string) => {
    e.preventDefault();
    if (buttonClicked === 'SORT'){
      console.log("Sort is Working");
      const newArray = this.state.sortArray;
      let temp = newArray[0];
      newArray[0] = newArray[1];
      newArray[1] = temp;
      this.setState((prevState: ISortVisualizerState) => {
        return {sortArray: newArray}
      });
    }
    else if (buttonClicked === 'RESET'){
      const sortCount = this.state.sortCount ?? 25;
      this.setState({...this.state,sortCount: sortCount});
    };
  }
  bubbleSort(array: number[]){
    const arr = array;
    for(let i = 0;i<arr.length; i++){
      for(let j=i+1; j< arr.length;j++){
        if (arr[i] > arr[j]){
          let temp = arr[i];
          arr[i] = arr[j];
          arr[j] = temp;
        }
      }
    }
    return arr;
  }
  handleArrayLengthChange = (e: any) => {
    e.preventDefault();
    const {value} = e.target;
    this.setState({sortCount: Number(Math.min(value, 100)) ?? 25})
  }
  render(){

  const sortArray: number[] = getNumberArray(this.state.sortCount);
  return (
    <>
    <div className='grid-column-3 w-3/5 bg-sorter min-h-1/2 border-2 border-white rounded-lg'>
      <div className='flex flex-row m-4 justify-evenly'>
          <div className='flex flex-col justify-start align-baseline'>
          <label htmlFor="count">Array Length</label>
            <input name='count'   className="my-2 border-2 border-black rounded-md w-24 h-10" type={'number'} min={10} max={100} onBlur={(e) => this.handleArrayLengthChange(e)}/>
          </div>
          <div>
            <Button  buttonLabel='Sort' emphasis='PRIMARY' size='sm' onClick={(e: any) => this.handleButtonClick(e, "SORT")}/>
          </div>
          <div>
            <Button  buttonLabel='Reset' emphasis='PRIMARY' size='sm' onClick={(e: any) => this.handleButtonClick(e, 'RESET')}/>
          </div>
      </div>
      <div className='h-1/2 w-11/12 justify-self-center'>
        <Sorter array={sortArray} type='bubble' sortClicked={this.state.sortClicked} />
      </div>
      <div className='justify-self-end'>
          <Button size = {'md'} emphasis={'PRIMARY'} buttonLabel={"Code Snippet</>"}  />
      </div>
    </div>
    </>
  )}
}

export default SortVisualizer
