import React, {useState} from 'react'
import Button from './Button'
import Sorter from './Sorter'


export interface ISortVisualizerProps{
  sortName: string;
}

export interface ISortVisualizerState{
  sortCount: number;
  sortArray: number[];
}




const SortVisualizer =(props: ISortVisualizerProps) => {

const getNumberArray = (length?: number) =>{
  const a: number[] = [];
  const arrayLength = length ?? 50;
  for(let i=0; i<arrayLength;i = i+1){
    a.push(Math.floor(Math.random() * (100)));
  }
  return a;
}

  const [state, setState] = useState({
    sortCount: 25,
    sortArray: getNumberArray()

  });

  const handleButtonClick = (e: any, buttonClicked: string) => {
    e.preventDefault();
    if (buttonClicked === 'SORT'){
      console.log("Sort is Working");
      const newArray = state.sortArray;
      setTimeout(() => {
        let temp = newArray[0];
        newArray[0] = newArray[1];
        newArray[1] = temp;
        setState(prevState => ({...prevState, sortArray: newArray}));
      },3000);
      console.log("sorted after 3 seconds");
    }
    else if (buttonClicked === 'RESET'){
      const sortCount = state.sortCount ?? 25;
      setState(prevState => ({...prevState, sortCount: sortCount}));
    };
  }

  /*const bubbleSort = (array: number[]) => {
    const arr = array;
    for(let i = 0;i<arr.length; i++){
      for(let j=i+1; j< arr.length;j++){
        if (arr[i] > arr[j]){
          let temp = arr[i];
          arr[i] = arr[j];
          arr[j] = temp;
        }
      }
    }
    return arr;
  }*/


  const handleArrayLengthChange = (e: any) => {
    e.preventDefault();
    const {value} = e.target;
    setState({sortCount: Math.min(value,100) , sortArray: getNumberArray(Math.min(value,100))});
}


  const { sortArray }= state;
  return (
    <>
    <div className='grid-column-3 w-3/5 bg-sorter min-h-1/2 border-2 border-white rounded-lg'>
      <div className='flex flex-row m-4 justify-evenly'>
          <div className='flex flex-col justify-start align-baseline'>
          <label htmlFor="count">Array Length</label>
            <input name='count'   className="my-2 border-2 border-black rounded-md w-24 h-10" type={'number'} min={10} max={100} onBlur={(e) => handleArrayLengthChange(e)}/>
          </div>
          <div>
            <Button  buttonLabel='Sort' emphasis='PRIMARY' size='sm' onClick={(e: any) => handleButtonClick(e, "SORT")}/>
          </div>
          <div>
            <Button  buttonLabel='Reset' emphasis='PRIMARY' size='sm' onClick={(e: any) => handleButtonClick(e, 'RESET')}/>
          </div>
      </div>
      <div className='h-1/2 w-11/12 justify-self-center'>
        <Sorter array={sortArray} type='bubble' />
      </div>
      <div className='justify-self-end'>
          <Button size = {'md'} emphasis={'PRIMARY'} buttonLabel={"Code Snippet</>"}  />
      </div>
    </div>
    </>
  )
}

export default SortVisualizer
